void setupFontrsmg(fontInfo *font){
	if(font != nullptr){
		font->fontIndex = 16;
		strncpy(font->fontName, "R SMG", 6);
		font->startUnicode = 32;
		font->endUnicode = 128;
		font->blockLength = 6;
		font->logicality = false;
		font->monospace = true;
		font->monoMask = 0xFF;
		font->index = nullptr;
		font->data = rsmg_data;
	}
}

